// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../interfaces/IDragonSwapTrigger.sol";

/**
 * @title MockDragonSwapTrigger
 * @dev Simple mock implementation of DragonSwapTrigger for testing VRF integration
 */
contract MockDragonSwapTrigger is IDragonSwapTrigger {
    // State variables
    IERC20 public wrappedToken;
    IERC20 public dragonToken;
    address public vrfConsumer;
    uint256 public minSwapAmount;
    uint256 public winThreshold = 1000; // 0.1% chance (1/1000)
    uint256 public swapCount;
    uint256 public jackpotBalance;

    // Events
    event SwapDetected(address indexed user, uint256 amount);
    event RandomnessRequested(uint256 indexed requestId, address indexed user);
    event JackpotIncreased(uint256 amount, uint256 newBalance);

    /**
     * @dev Constructor
     */
    constructor(
        address _wrappedToken,
        address _dragonToken,
        address _vrfConsumer,
        uint256 _minSwapAmount
    ) {
        wrappedToken = IERC20(_wrappedToken);
        dragonToken = IERC20(_dragonToken);
        vrfConsumer = _vrfConsumer;
        minSwapAmount = _minSwapAmount;
    }

    /**
     * @notice Get native token wrapper address
     * @return Native token wrapper address
     */
    function getNativeTokenWrapper() external view override returns (address) {
        return address(wrappedToken);
    }

    /**
     * @notice Triggered when a user swaps native token for DRAGON
     * @param _user The user who performed the swap
     * @param _amount The amount of native token swapped
     */
    function onSwapNativeTokenToDragon(address _user, uint256 _amount) external override {
        if (_amount < minSwapAmount) {
            return;
        }

        swapCount++;

        // Simulate the swap by transferring tokens from user to this contract
        wrappedToken.transferFrom(_user, address(this), _amount);

        // Emit the swap event
        emit SwapDetected(_user, _amount);

        // Call VRF consumer to request randomness
        if (vrfConsumer != address(0)) {
            // We would normally need to cast this to interface, but for testing we can just check it's not zero
            // In a real contract, this would call the VRF consumer
            (bool success, ) = vrfConsumer.call(
                abi.encodeWithSignature("requestRandomness(address)", _user)
            );

            if (success) {
                emit RandomnessRequested(swapCount, _user);
            }
        }
    }

    /**
     * @notice Add funds to the jackpot
     * @param _amount The amount to add
     */
    function addToJackpot(uint256 _amount) external override {
        // Transfer tokens to this contract
        wrappedToken.transferFrom(msg.sender, address(this), _amount);

        // Update jackpot balance
        jackpotBalance += _amount;

        emit JackpotIncreased(_amount, jackpotBalance);
    }

    /**
     * @notice Get the current jackpot balance
     * @return The jackpot balance
     */
    function getJackpotBalance() external view override returns (uint256) {
        return jackpotBalance;
    }

    /**
     * @notice Set the win threshold
     * @param _winThreshold The new win threshold
     */
    function setWinThreshold(uint256 _winThreshold) external override {
        winThreshold = _winThreshold;
    }

    /**
     * @notice Set the minimum swap amount
     * @param _minSwapAmount The new minimum swap amount
     */
    function setMinSwapAmount(uint256 _minSwapAmount) external override {
        minSwapAmount = _minSwapAmount;
    }

    /**
     * @notice Set VRF consumer
     * @param _vrfConsumerAddress New VRF consumer address
     */
    function setVRFConsumer(address _vrfConsumerAddress) external override {
        vrfConsumer = _vrfConsumerAddress;
    }

    /**
     * @notice Process randomness from VRF consumer
     * @dev VRF consumer will call this function
     */
    function fulfillRandomness(uint256 /* _requestId */, uint256 /* _randomness */) external view override {
        // Implementation
    }
}

