contracts/test/SimplifiedDragon.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title SimplifiedDragon
 * @dev Simplified implementation of the Dragon token with fee mechanics
 * for TypeScript deployment testing
 */
contract SimplifiedDragon is ERC20, Ownable {
    using SafeERC20 for IERC20;

    // Token references
    address public jackpotVault;
    address public ve69LPFeeDistributor;
    address public wrappedSonicAddress;

    // Fee structure
    struct Fees {
        uint256 jackpotFee; // Fee for jackpot (basis points)
        uint256 ve69LPFee;  // Fee for ve69LP (basis points)
        uint256 burnFee;    // Fee for burning (basis points)
        uint256 totalFee;   // Total fee (basis points)
    }

    // Fixed fee values according to tokenomics
    Fees public buyFees = Fees(690, 241, 69, 1000);  
    Fees public sellFees = Fees(690, 241, 69, 1000); 

    // Excluded from fees
    mapping(address => bool) public isExcludedFromFees;

    // Events
    event FeesUpdated(string feeType, uint256 jackpotFee, uint256 ve69LPFee, uint256 burnFee, uint256 totalFee);
    event FeeTransferred(address indexed recipient, uint256 amount, string feeType);

    /**
     * @dev Constructor to initialize the token
     * @param name_ Name of the token
     * @param symbol_ Symbol of the token
     * @param initialSupply Initial supply of tokens
     * @param _jackpotVault Address of the jackpot vault
     * @param _ve69LPFeeDistributor Address of the ve69LP fee distributor
     * @param _wrappedSonicAddress Address of the wS token
     */
    constructor(
        string memory name_,
        string memory symbol_,
        uint256 initialSupply,
        address _jackpotVault,
        address _ve69LPFeeDistributor,
        address _wrappedSonicAddress
    ) ERC20(name_, symbol_) Ownable() {
        require(_jackpotVault != address(0), "Jackpot vault cannot be zero address");
        require(_ve69LPFeeDistributor != address(0), "ve69LP fee distributor cannot be zero address");
        require(_wrappedSonicAddress != address(0), "Wrapped Sonic address cannot be zero address");

        // Set addresses
        jackpotVault = _jackpotVault;
        ve69LPFeeDistributor = _ve69LPFeeDistributor;
        wrappedSonicAddress = _wrappedSonicAddress;

        // Exclude from fees
        _excludeFromFees(address(this), true);
        _excludeFromFees(msg.sender, true);
        _excludeFromFees(_jackpotVault, true);
        _excludeFromFees(_ve69LPFeeDistributor, true);

        // Mint initial supply
        _mint(msg.sender, initialSupply);
    }

    /**
     * @dev Excludes an account from fees
     * @param account Address to exclude
     * @param excluded Whether to exclude or include
     */
    function excludeFromFees(address account, bool excluded) external onlyOwner {
        _excludeFromFees(account, excluded);
    }

    /**
     * @dev Internal function to exclude an account from fees
     * @param account Address to exclude from fees
     * @param excluded Boolean indicating if the account should be excluded
     */
    function _excludeFromFees(address account, bool excluded) internal {
        isExcludedFromFees[account] = excluded;
    }

    /**
     * @dev Sets the buy fees
     * @param _jackpotFee Fee to jackpot
     * @param _ve69LPFee Fee to ve69LP
     * @param _burnFee Fee to burn
     */
    function setBuyFees(
        uint256 _jackpotFee,
        uint256 _ve69LPFee,
        uint256 _burnFee
    ) external onlyOwner {
        require(_jackpotFee + _ve69LPFee + _burnFee <= 2000, "Total fee cannot exceed 20%");

        buyFees.jackpotFee = _jackpotFee;
        buyFees.ve69LPFee = _ve69LPFee;
        buyFees.burnFee = _burnFee;
        buyFees.totalFee = _jackpotFee + _ve69LPFee + _burnFee;

        emit FeesUpdated("Buy", _jackpotFee, _ve69LPFee, _burnFee, buyFees.totalFee);
    }

    /**
     * @dev Sets the sell fees
     * @param _jackpotFee Fee to jackpot
     * @param _ve69LPFee Fee to ve69LP
     * @param _burnFee Fee to burn
     */
    function setSellFees(
        uint256 _jackpotFee,
        uint256 _ve69LPFee,
        uint256 _burnFee
    ) external onlyOwner {
        require(_jackpotFee + _ve69LPFee + _burnFee <= 2000, "Total fee cannot exceed 20%");

        sellFees.jackpotFee = _jackpotFee;
        sellFees.ve69LPFee = _ve69LPFee;
        sellFees.burnFee = _burnFee;
        sellFees.totalFee = _jackpotFee + _ve69LPFee + _burnFee;

        emit FeesUpdated("Sell", _jackpotFee, _ve69LPFee, _burnFee, sellFees.totalFee);
    }

    /**
     * @dev Gets the buy fees
     * @return jackpotFee Fee to jackpot
     * @return ve69LPFee Fee to ve69LP
     * @return burnFee Fee to burn
     * @return totalFee Total fee
     */
    function getBuyFees() external view returns (
        uint256 jackpotFee,
        uint256 ve69LPFee,
        uint256 burnFee,
        uint256 totalFee
    ) {
        return (buyFees.jackpotFee, buyFees.ve69LPFee, buyFees.burnFee, buyFees.totalFee);
    }

    /**
     * @dev Gets the sell fees
     * @return jackpotFee Fee to jackpot
     * @return ve69LPFee Fee to ve69LP
     * @return burnFee Fee to burn
     * @return totalFee Total fee
     */
    function getSellFees() external view returns (
        uint256 jackpotFee,
        uint256 ve69LPFee,
        uint256 burnFee,
        uint256 totalFee
    ) {
        return (sellFees.jackpotFee, sellFees.ve69LPFee, sellFees.burnFee, sellFees.totalFee);
    }

    /**
     * @dev Mints new tokens
     * @param to Address to mint tokens to
     * @param amount Amount to mint
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    /**
     * @dev Burns tokens from caller
     * @param amount Amount to burn
     */
    function burn(uint256 amount) external {
        _burn(msg.sender, amount);
    }
}

contracts/test/SimplifiedJackpotVault.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title SimplifiedJackpotVault
 * @dev Simplified jackpot vault for Dragon ecosystem
 */
contract SimplifiedJackpotVault is Ownable {
    using SafeERC20 for IERC20;

    address public wrappedSonic;
    address public dragonToken;
    uint256 public jackpotAmount;

    event JackpotIncreased(uint256 amount, uint256 newTotal);
    event JackpotAwarded(address indexed winner, uint256 amount);
    event TokenAddressSet(address tokenAddress);

    /**
     * @dev Constructor to initialize the vault with Wrapped Sonic address
     * @param _wrappedSonic The address of the Wrapped Sonic token
     * @param _owner The owner of the contract
     */
    constructor(address _wrappedSonic, address _owner) {
        require(_wrappedSonic != address(0), "Invalid wS address");
        wrappedSonic = _wrappedSonic;
        transferOwnership(_owner);
    }

    /**
     * @dev Sets the Dragon token address
     * @param _tokenAddress The Dragon token address
     */
    function setTokenAddress(address _tokenAddress) external onlyOwner {
        require(_tokenAddress != address(0), "Invalid token address");
        dragonToken = _tokenAddress;
        emit TokenAddressSet(_tokenAddress);
    }

    /**
     * @dev Adds funds to the jackpot
     * @param _amount The amount to add
     */
    function addToJackpot(uint256 _amount) external {
        require(_amount > 0, "Amount must be greater than 0");

        IERC20(wrappedSonic).safeTransferFrom(msg.sender, address(this), _amount);
        jackpotAmount += _amount;

        emit JackpotIncreased(_amount, jackpotAmount);
    }

    /**
     * @dev Awards jackpot to a winner
     * @param _winner The winner's address
     * @param _amount The amount to award
     */
    function awardJackpot(address _winner, uint256 _amount) external onlyOwner {
        require(_winner != address(0), "Invalid winner address");
        require(_amount > 0, "Amount must be greater than 0");
        require(_amount <= jackpotAmount, "Insufficient jackpot funds");

        jackpotAmount -= _amount;
        IERC20(wrappedSonic).safeTransfer(_winner, _amount);

        emit JackpotAwarded(_winner, _amount);
    }

    /**
     * @dev Returns the current jackpot amount
     * @return The jackpot amount
     */
    function getJackpotAmount() external view returns (uint256) {
        return jackpotAmount;
    }
}

contracts/test/Simplifiedve69LPFeeDistri.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title Simplifiedve69LPFeeDistributor
 * @dev Simplified ve69LP fee distributor for Dragon ecosystem
 */
contract Simplifiedve69LPFeeDistributor is Ownable {
    using SafeERC20 for IERC20;

    address public ve69LPToken;
    address public wrappedSonic;
    uint256 public totalDistributed;
    uint256 public epoch;

    mapping(uint256 => uint256) public epochRewards;
    mapping(address => uint256) public lastClaimedEpoch;

    event RewardsDistributed(uint256 amount, uint256 epoch);
    event RewardsClaimed(address indexed user, uint256 amount, uint256 fromEpoch, uint256 toEpoch);

    /**
     * @dev Constructor to initialize the distributor
     * @param _ve69LPToken The ve69LP token address
     * @param _wrappedSonic The Wrapped Sonic token address
     */
    constructor(address _ve69LPToken, address _wrappedSonic) {
        require(_ve69LPToken != address(0), "Invalid ve69LP address");
        require(_wrappedSonic != address(0), "Invalid wS address");
        ve69LPToken = _ve69LPToken;
        wrappedSonic = _wrappedSonic;
        epoch = 1;
    }

    /**
     * @dev Distributes rewards to the contract for the current epoch
     * @param _amount The amount to distribute
     */
    function receiveRewards(uint256 _amount) external {
        require(_amount > 0, "Amount must be greater than 0");

        IERC20(wrappedSonic).safeTransferFrom(msg.sender, address(this), _amount);
        epochRewards[epoch] += _amount;
        totalDistributed += _amount;

        emit RewardsDistributed(_amount, epoch);
    }

    /**
     * @dev Advances to the next epoch
     */
    function advanceEpoch() external onlyOwner {
        epoch += 1;
    }

    /**
     * @dev Claims rewards for a user
     * @param _user The user's address
     */
    function claimRewards(address _user) external {
        uint256 userLastEpoch = lastClaimedEpoch[_user];
        if (userLastEpoch == 0) {
            userLastEpoch = 1;
        }

        require(userLastEpoch < epoch, "No new rewards to claim");

        uint256 totalReward = 0;
        for (uint256 i = userLastEpoch; i < epoch; i++) {
            totalReward += calculateUserReward(_user, i);
        }

        require(totalReward > 0, "No rewards to claim");

        lastClaimedEpoch[_user] = epoch;
        IERC20(wrappedSonic).safeTransfer(_user, totalReward);

        emit RewardsClaimed(_user, totalReward, userLastEpoch, epoch - 1);
    }

    /**
     * @dev Calculates a user's reward for a specific epoch
     * @param _user The user's address
     * @param _epoch The epoch number
     * @return The reward amount
     */
    function calculateUserReward(address _user, uint256 _epoch) public view returns (uint256) {
        // Simplified calculation - in a real implementation, this would
        // check ve69LP balances and calculate proportional rewards
        if (epochRewards[_epoch] == 0) {
            return 0;
        }

        // Dummy implementation - assumes user gets 10% of epoch rewards
        return epochRewards[_epoch] / 10;
    }
}

