     * @return price Price value
     */
    function testTickToPrice(int24 tick) external pure returns (uint256 price) {
        return UniswapV3PriceUtils.tickToPrice(tick);
    }

    /**
     * @dev Test the sqrtPriceX96ToPrice function
     * @param sqrtPriceX96 sqrtPriceX96 value
     * @return price Price value
     */
    function testSqrtPriceX96ToPrice(uint160 sqrtPriceX96) external pure returns (uint256 price) {
        return UniswapV3PriceUtils.sqrtPriceX96ToPrice(sqrtPriceX96);
    }

    /**
     * @dev Test the tickToSqrtPriceX96 function
     * @param tick Tick value
     * @return sqrtPriceX96 sqrtPriceX96 value
     */
    function testTickToSqrtPriceX96(int24 tick) external pure returns (uint160 sqrtPriceX96) {
        return UniswapV3PriceUtils.tickToSqrtPriceX96(tick);
    }

    /**
     * @dev Test the calculateTokenRatios function
     * @param currentTick Current tick
     * @param lowerTick Lower tick
     * @param upperTick Upper tick
     * @return token0Percent Percentage of token0
     * @return token1Percent Percentage of token1
     */
    function testCalculateTokenRatios(
        int24 currentTick,
        int24 lowerTick,
        int24 upperTick
    ) external pure returns (uint8 token0Percent, uint8 token1Percent) {
        return UniswapV3PriceUtils.calculateTokenRatios(currentTick, lowerTick, upperTick);
    }

    /**
     * @dev Test the calculateTokenRatiosFromSqrtPrice function
     * @param sqrtPriceX96 Current sqrtPriceX96
     * @param sqrtPriceX96A Lower sqrtPriceX96
     * @param sqrtPriceX96B Upper sqrtPriceX96
     * @return token0Percent Percentage of token0
     * @return token1Percent Percentage of token1
     */
    function testCalculateTokenRatiosFromSqrtPrice(
        uint160 sqrtPriceX96,
        uint160 sqrtPriceX96A,
        uint160 sqrtPriceX96B
    ) external pure returns (uint8 token0Percent, uint8 token1Percent) {
        return UniswapV3PriceUtils.calculateTokenRatiosFromSqrtPrice(
            sqrtPriceX96,
            sqrtPriceX96A,
            sqrtPriceX96B
        );
    }

    /**
     * @dev Test the calculateLiquidityRange function
     * @param lowerTick Lower tick
     * @param upperTick Upper tick
     * @return rangePercentage Range percentage in basis points
     */
    function testCalculateLiquidityRange(
        int24 lowerTick,
        int24 upperTick
    ) external pure returns (uint256 rangePercentage) {
        return UniswapV3PriceUtils.calculateLiquidityRange(lowerTick, upperTick);
    }

    /**
     * @dev Test the calculateAPR function
     * @param amountUSD Amount of liquidity in USD
     * @param dailyVolumeUSD Daily volume in USD
     * @param feeTier Fee tier in basis points
     * @return apr Annual percentage rate in basis points
     */
    function testCalculateAPR(
        uint256 amountUSD,
        uint256 dailyVolumeUSD,
        uint24 feeTier
    ) external pure returns (uint256 apr) {
        return UniswapV3PriceUtils.calculateAPR(amountUSD, dailyVolumeUSD, feeTier);
    }
}

